$(document).ready(function(){$(window).resize(function(){var h=$(window).height(),offsetTop=60;$('#map-canvas').css('height',(h-offsetTop))}).resize()});var bldgsList=[{"latlng":[36.362368,-86.497277],name:"Ramer Admin Building",main:1},{"latlng":[36.363101,-86.498032],name:"Wood Campus Center",main:1},{"latlng":[36.362379,-86.498191],name:"Caudill Hall",main:1},{"latlng":[36.363281,-86.496656],name:"Warf Building",main:1},{"latlng":[36.364172,-86.496445],name:"Pickel Field House",main:1},{"latlng":[36.364063,-86.497516],name:"Thigpen Library",main:1},{"latlng":[36.363675,-86.498420],name:"Steinhauer-Rogan-Black (SRB) Humanities",main:1},{"latlng":[36.364376,-86.499008],name:"Mattox Building",main:1},{"latlng":[36.364923,-86.498108],name:"Wallace South",main:1},{"latlng":[36.365203,-86.497649],name:"Wallace North",main:1},{"latlng":[36.365381,-86.494232],name:"Gibson Hall",main:1},{"latlng":[36.364438,-86.493947],name:"100 Building",main:1},{"latlng":[36.364860,-86.493911],name:"200 Building",main:1},{"latlng":[36.364795,-86.493252],name:"300 Building",main:1},{"latlng":[36.365369,-86.493621],name:"400 Building",main:1}];var infoWnd=new google.maps.InfoWindow();var markerController=new google.maps.MVCObject();function initialize(){var mapDiv=document.getElementById("map_canvas");var myOptions={zoom:8,mapTypeId:google.maps.MapTypeId.HYBRID};mapCanvas=new google.maps.Map(mapDiv,myOptions);var i,choice=[document.getElementById("main")];for(i=0;i<choice.length;i++){google.maps.event.addDomListener(choice[i],"click",selectChanged)}
var bounds=new google.maps.LatLngBounds();var campus,latlng;for(i=0;i<bldgsList.length;i++){campus=bldgsList[i];latlng=new google.maps.LatLng(campus.latlng[0],campus.latlng[1]);bounds.extend(latlng);var marker=createMarker({map:mapCanvas,position:latlng,others:campus});createMarkerButton(marker)}
mapCanvas.fitBounds(bounds);markerController.set("select","main");google.maps.event.trigger(choice[0],"click")}
function selectChanged(){var selectedVal=this.value;markerController.set("select",selectedVal);var i,ul,listNames=["main"];for(i=0;i<listNames.length;i++){ul=document.getElementById(listNames[i]+"_list");if(listNames[i]===selectedVal){ul.style.display="block"}else{ul.style.display="none"}}}
function createMarker(params){var marker=new google.maps.Marker(params);google.maps.event.addListener(marker,"click",function(){infoWnd.setContent("<strong>"+params.others.name+"</strong>");infoWnd.open(params.map,marker)});marker.bindTo("select",markerController,"select");google.maps.event.addListener(marker,"select_changed",changeMarkerVisibility);return marker}
function changeMarkerVisibility(){var marker=this;var others=marker.get("others");var selectedVal=marker.get("select");marker.setVisible(others[selectedVal]?!0:!1)}
function createMarkerButton(marker){var others=marker.get("others"),i,name,ul,li,listNames=["main"];for(i=0;i<listNames.length;i++){name=listNames[i];if(others[name]){ul=document.getElementById(name+"_list");li=document.createElement("li");li.innerHTML=others.name;ul.appendChild(li);google.maps.event.addDomListener(li,"click",function(){google.maps.event.trigger(marker,"click")})}}}
google.maps.event.addDomListener(window,"load",initialize)